 */
import { Pressable, StyleSheet, Text, View } from "@bacons/react-views";
import React from "react";

import { useSelectedLog } from "../Data/LogContext";
import * as LogBoxStyle from "../UI/LogBoxStyle";

type Props = {
  onDismiss: () => void;
  onMinimize: () => void;
};

export function LogBoxInspectorFooter(props: Props) {
  const log = useSelectedLog();

  if (log.level === "syntax") {
    return (
      <View style={styles.root}>
        <View style={styles.button}>
          <Text style={styles.syntaxErrorText}>
            This error cannot be dismissed.
          </Text>
        </View>
      </View>
    );
  }

  return (
    <View style={styles.root}>
      <FooterButton text="Dismiss" onPress={props.onDismiss} />
      <FooterButton text="Minimize" onPress={props.onMinimize} />
    </View>
  );
}

function FooterButton({
  text,
  onPress,
}: {
  onPress: () => void;
  text: string;
}) {
  return (
    <Pressable onPress={onPress} style={{ flex: 1 }}>
      {({ hovered, pressed }) => (
        <View
          style={[
            buttonStyles.safeArea,
            {
              transitionDuration: "150ms",
              backgroundColor: pressed
                ? "#323232"
                : hovered
                ? "#111111"
                : LogBoxStyle.getBackgroundColor(),
            },
          ]}
        >
          <View style={buttonStyles.content}>
            <Text selectable={false} style={buttonStyles.label}>
              {text}
            </Text>
          </View>
        </View>
      )}
    </Pressable>
  );
}

const buttonStyles = StyleSheet.create({
  safeArea: {
    flex: 1,
    borderTopWidth: 1,
    borderColor: "#323232",
    // paddingBottom: DeviceInfo.getConstants().isIPhoneX_deprecated ? 30 : 0,
  },
  content: {
    alignItems: "center",
    height: 48,
    justifyContent: "center",
  },
  label: {
    color: LogBoxStyle.getTextColor(1),
    fontSize: 14,
    includeFontPadding: false,
    lineHeight: 20,
  },
});

const styles = StyleSheet.create({
  root: {
    backgroundColor: LogBoxStyle.getBackgroundColor(1),
    shadowColor: "#000",
    shadowOffset: { width: 0, height: -2 },
    shadowRadius: 2,
    shadowOpacity: 0.5,
    flexDirection: "row",
  },
  button: {
    flex: 1,
  },
  syntaxErrorText: {
    textAlign: "center",
    width: "100%",
    height: 48,
    fontSize: 14,
    lineHeight: 20,
    paddingTop: 20,
    paddingBottom: 50,
    fontStyle: "italic",
    color: LogBoxStyle.getTextColor(0.6),
  },
});
